package arrays.demo;

public class OneDStringArrayExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[] name = new String[3];
		name[0]="Aditya";
		name[1]=" ";
		name[2]="Dua";
		
		
		for(int i=0;i<name.length;i++){
			System.out.print(name[i]);
		}
		
	}

}
